// Set components to equal height
$.fn.equalHeight = function() {
	var maxHeight = 0;
	return this.each(function(index, box) {
		var boxHeight = $(box).outerHeight();
		maxHeight = Math.max(maxHeight, boxHeight);
	}).outerHeight(maxHeight);
};

// trackCampaign.js, v1.0
//
// Campaign tracking query string parameter passing javascript.
// It will look for the following query string parameters:
//
//     cid
//     utm_campaign
//     utm_source
//
// It will update all links in components and grids that have a class of "passCID"

// Pass in a link, and it will be updated with the passed parameter name and value
function replaceQueryParam(oldLink, qParam, newValue) {
	var addString = false;
	var addParam = true;
	var replaceParam = false;
	var newLink;
	//
	// Separate oldLink page and query string
	var page = oldLink.split("?");
	//
	// If oldLink has no query string, will need to append new query string
	if ((page[1] =="") || (typeof page[1] == 'undefined')) {
		addString = true;
		addParam = false;
	}
	// Else pull apart query string to look for value to replace
	else {
		var vars = page[1].split("&");
		for (var i = 0; i < vars.length; i++) {
			var pair = vars[i].split("=");
			var qName = pair[0].toLowerCase();
			if (qName == qParam) {
				vars[i] = pair[0] + "=" + newValue;
				replaceParam = true;
				addParam = false;
			}
		}
	}
	if (replaceParam || addParam || addString) {
		// Simple append of query parameter name/value pair
		if (addParam || addString) {
			var delimiter = "?";
			if (addParam) {
				delimiter = "&";
			}
			newLink = oldLink + delimiter + qParam + "=" + newValue;
		}
		// Update query string with replacement of query parameter value
		else {
			newLink = page[0] + "?";
			for (var p = 0; p < vars.length; p++) {
				newLink += vars[p];
				if (p < vars.length-1) {
					newLink += "&";
				}
			}
		}
		// return the modified link URL
		return newLink;
	}
	// False condition to skip updating the link URL
	else {
		return(false);
	}
}

// After page loads...
jQuery(document).ready(function() {
	var queryString = window.location.search.substring(1);
	// If page URL has query string...
	if (queryString) {
		// Define URL and link query string parameter keys to look for
		var queryParams = ["cid", "utm_campaign", "utm_source"];
		var queryValues = [];
		//
		// Add array elements to make queryParams and queryValues equal length
		for (var n = 0; n < queryParams.length; n++) {
			queryValues[n] = "";
		}
		// Pull apart page URL to look for query string parameters to update
		var vars = queryString.split("&");
		for (var i = 0; i < vars.length; i++) {
			var pair = vars[i].split("=");
			pair[0] = pair[0].toLowerCase();
			// For each URL query string parameter key to look for...
			for (var j = 0; j < queryParams.length; j++) { 
				// Look for matching query paramater names in page URL
				if (pair[0] == queryParams[j]) {
					queryValues[j] = pair[1];
				}
			}
		}
		jQuery(".passCID a").each(function() {
			// Get the value of each HREF in turn
			var newLink = jQuery(this).attr("href");
			// For each URL query string parameter key to look for...
			for (var m = 0; m < queryParams.length; m++) { 
				// If we have a value to replace in the HREF...
				if (queryValues[m]) {
					// Replace the value in Links
					newLink = replaceQueryParam(newLink, queryParams[m], queryValues[m]);
				}
			}
			// Now update the link URL to pass updated campaign tracking query parameters
			if (newLink) {
				jQuery(this).attr("href", newLink);
			}
		});
	}
});

