// util function to check if an element has a scrollbar present
$.fn.hasScrollBar = function(direction) {
	if (this.length > 0) {
		if (direction === 'vertical') {
			return this.get(0).scrollHeight > this.innerHeight();
		}
		else if (direction === 'horizontal') {
			return this.get(0).scrollWidth > this.innerWidth();
		}
		return false;
	}
};

function calculateTopMargin(activeLbox) {
	viewportHeight = jQuery(window).innerHeight();
	if (jQuery(window).width() >= 480 ) {
		jQuery(activeLbox + ' .c08').css('height', (viewportHeight*0.8));
		c08Heght = jQuery(activeLbox + ' .c08').outerHeight();
		contentHeight = ((c08Heght - jQuery(activeLbox + ' .c08_title').outerHeight())-32);
		jQuery(activeLbox + ' .c08_content').css('height', contentHeight);
		if (!jQuery(activeLbox + ' .c08_content').hasScrollBar('vertical')) {
			jQuery(activeLbox + ' .c08_content').css('height', 'auto');
			jQuery(activeLbox + ' .c08').css('height', 'auto');
			c08Heght = jQuery(activeLbox + ' .c08').outerHeight();
		}
		marginTop = (viewportHeight - c08Heght)/2;
		jQuery(activeLbox + ' .c08').css('margin-top', marginTop);
		jQuery(activeLbox + ' .c08').css('top', 'auto');
	} else {
		marginTop = jQuery(activeLbox + ' .c08').css('margin-right');
		jQuery(activeLbox + ' .c08').css('margin-top', marginTop);
		jQuery(activeLbox + ' .c08').css({'height' : (viewportHeight-26)});
		c08Heght = jQuery(activeLbox + ' .c08').outerHeight();
		contentHeight = ((c08Heght - jQuery('.c08_title').outerHeight())-32);
		jQuery(activeLbox + ' .c08_content').css('height', contentHeight);
		if (!jQuery(activeLbox + ' .c08_content').hasScrollBar('vertical')) {
			jQuery(activeLbox + ' .c08_content').css('height', 'auto');
			jQuery(activeLbox + ' .c08').css('height', 'auto');
			c08Heght = jQuery(activeLbox + ' .c08').outerHeight();
		}
	}
}

function closec08() {
	jQuery("html,body").css("overflow","auto");
	jQuery('.c08_modal').fadeOut(200, function(){
		if (jQuery('.c08_modal.is-open').attr('id') === 'c08_modal-video') {
			jQuery('#c08_video-mp4').attr('src', '');
			jQuery('#c08_video-webm').attr('src', '');
			jQuery('#c08_video-ogg').attr('src', '');
			jQuery('.c08_video')[0].load();
		}
		jQuery(activeLbox).removeClass('is-open');
	});
}

