$(function(){

    //history statechange listener interprets modified URL and performs AJAX reqs
    History.Adapter.bind(window,'statechange',function(){
        var state = History.getState();
        var ajaxUrl = $('.searchResults').attr('data-ajaxurl')+'.html';
        var params;
        if (state.cleanUrl.indexOf("?")!=-1) {
            //peel params and apply to ajaxurl to perform ajax
            params = state.cleanUrl.substring(state.cleanUrl.indexOf("?"));
            ajaxUrl += params;
            $('.searchResults').removeClass('errored').addClass('loading');
            $.ajax({url:ajaxUrl}).done(function(d){
                var $div = $("<div></div>");
                $div.html(d);
                $('.searchResults').html($div.find('.searchResults').html())
                $('.searchResults').removeClass('loading');
                window.scrollTo(0,0);
            }).fail(function(){
                $('.searchResults').removeClass('loading').addClass('errored');
            });

        }
    })

    //ajax links push history state
    $(document).on('click', '.ajaxlink', function(e){
        e.preventDefault();
        var href = $(e.target).attr('href');
        var params;
        if (href.indexOf("?") != -1) {
            params = href.substring(href.indexOf("?"));
            History.pushState(null,null,params);
        }

    });

    //ajax links push history state
	$(document).on('change', '.ajaxselect', function(e){
		e.preventDefault();
		var selectedOption = $(e.target).find(":selected").each ( function() {
			var params;
			var href = $(this).attr('href');
			if (href.indexOf("?") != -1) {
				params = href.substring(href.indexOf("?"));
				History.pushState(null,null,params);
			}
	    });
	});

});

